import React from 'react';
import ReactDOM from 'react-dom';

import './menu.css'
import './main.css'

export class MenuBrick extends React.Component {
  render() {
    return(<div class="menuBrick">{this.props.children}</div>);
  }
}

export class MenuContainer extends React.Component {

  constructor(props) {
    super(props);
  }

  componentDidMount(...args) {
    window.addEventListener('scroll', this.handleScroll);
  }

  componentWillUnmount(...args) {
    window.removeEventListener('scroll', this.handleScroll);
  }

  render() {
    console.log(this.val)
    return(<div id="menu" class="menuContainer">
            {this.props.menuList.map(name => <MenuBrick>{name}</MenuBrick>)}
          </div>);
  }

  handleScroll(){
    var offY = document.getElementById("menu").offsetTop;
    var winY = window.pageYOffset;
    console.log(offY);
    console.log(winY);
    //var bounds = this.selector.current.getBoundingClientRect();
    //console.log(bounds);
  }
}

export class Header extends React.Component {
  render() {
    return(
      <div className="header">
        {this.props.children}
      </div>
    );
  }
}

export class MainPane extends React.Component {
  render(){
    return(<div className="mainPane">
            {this.props.children}
          </div>);
  }
}

export class PaneHeader extends React.Component {
  render(){
    return(<div></div>);
  }
}

export class SubPane extends React.Component {
  render(){
    return(<div></div>);
  }
}
