import React from 'react';

import './menu.css'
import './main.css'

export class MenuBrick extends React.Component {
  render() {
    return(<div class="menuBrick">{this.props.children}</div>);
  }
}

export class MenuContainer extends React.Component {
  componentDidMount(...args) {
    window.addEventListener('scroll', this.handleScroll);
  }

  componentWillUnmount(...args) {
    window.removeEventListener('scroll', this.handleScroll);
  }

  render() {
    return(<div class="menuContainer">
            {this.props.menuList.map(name => <MenuBrick>{name}</MenuBrick>)}
          </div>);
  }

  handleScroll(){
    console.log("przewijamy okno - offset : " + ReactDOM.findDOMNode(this).getBoundingClientRect())
  }
}

export class Header extends React.Component {
  render() {
    return(
      <div className="header">{this.props.children}</div>
    );
  }
}

export class MainPane extends React.Component {
  render(){
    return(<div className="mainPane">
            {this.props.children}
          </div>);
  }
}

export class PaneHeader extends React.Component {
  render(){
    return(<div></div>)
  }
}

export class SubPane extends React.Component {
  render(){
    return(<div></div>)
  }
}
