import React from 'react';
import ReactDOM from 'react-dom';

import './menu.css'
import './main.css'

export class MenuBrick extends React.Component {
  render() {
    return(<div class="menuBrick">{this.props.children}</div>);
  }
}

export class MenuContainer extends React.Component {
  constructor(props) {
    super(props);
    this.ref = React.createRef();
  }

  componentDidMount(...args) {
    window.addEventListener('scroll', this.handleScroll);
  }

  componentWillUnmount(...args) {
    window.removeEventListener('scroll', this.handleScroll);
  }

  render() {
    console.log("reference" + this.ref);
    return(<div class="menuContainer" ref={this.ref} >
            {this.props.menuList.map(name => <MenuBrick>{name}</MenuBrick>)}
          </div>);
  }

  handleScroll(){
    var bounds = this.ref.current.getBoundingClientRect();
    console.log(bounds);
    console.log("przewijamy okno - offset : " + window.pageYOffset)
  }
}

export class Header extends React.Component {
  render() {
    return(
      <div className="header">
        {this.props.children}
      </div>
    );
  }
}

export class MainPane extends React.Component {
  render(){
    return(<div className="mainPane">
            {this.props.children}
          </div>);
  }
}

export class PaneHeader extends React.Component {
  render(){
    return(<div></div>);
  }
}

export class SubPane extends React.Component {
  render(){
    return(<div></div>);
  }
}
