import React from 'react';
import ReactDOM from 'react-dom';
import {
  Route,
  NavLink,
  HashRouter
} from "react-router-dom";

import Home from './Home.js'
import About from './About.js'

import './css/menu.css'
import './css/main.css'
import './css/pane.css'

export class MenuBrick extends React.Component {
  render() {
    var opt = this.props.children
    console.log(opt.name)
    return(<div class="menuBrick">
            <NavLink to={opt.id}>{opt.name}</NavLink>
          </div>);
  }
}

export class MenuContainer extends React.Component {

  constructor(props) {
    super(props);
  }

  componentDidMount(...args) {
    window.addEventListener('scroll', this.handleScroll);
  }

  componentWillUnmount(...args) {
    window.removeEventListener('scroll', this.handleScroll);
  }

  render() {
    return(<div id="menu" class="menuContainer">
            {this.props.menuList.map(object => <MenuBrick>{object}</MenuBrick>)}
          </div>);
  }

  handleScroll(){
    var menuContainer = document.getElementById("menu");

    if (window.pageYOffset > menuContainer.offsetTop) {
      menuContainer.classList.add("menuContainerSticky");
    } else {
      menuContainer.classList.remove("menuContainerSticky");
    }
  }
}

export class Header extends React.Component {
  render() {
    return(
      <div className="header">
        {this.props.children}
      </div>
    );
  }
}

